package org.faroukabichou.kustomize

import App
import android.os.Bundle
import android.util.Base64
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.compose.ui.text.font.FontFamily
import androidx.compose.ui.text.googlefonts.Font
import androidx.compose.ui.text.googlefonts.GoogleFont

class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        val devCert = ""
        val prodCert = ""

        val devCertBytes = Base64.decode(devCert, Base64.DEFAULT)
        val prodCertBytes = Base64.decode(prodCert, Base64.DEFAULT)

        val certsList: List<List<ByteArray>> = listOf(
            listOf(devCertBytes),
            listOf(prodCertBytes)
        )
        val provider = GoogleFont.Provider(
            providerAuthority = "com.google.android.gms.fonts",
            providerPackage = "com.google.android.gms",
            certificates = certsList
        )
        val fontName = GoogleFont("Lobster Two")
        val fontFamily = FontFamily(
            Font(googleFont = fontName, fontProvider = provider)
        )
        setContent {
            App(
                typography = androidx.compose.material.Typography(
                    defaultFontFamily = fontFamily
                )
            )
        }
    }
}